import { createReducer, on, Action } from "@ngrx/store";
import { NoticeData, initialNoticeDataState, initialNoticeDataFiltersState } from "./notice.state";
import { DynamicQueryPart, WithId } from "../state";
import { searchNoticeData, searchNoticeDataSuccess, searchNoticeDataError, changeNoticeDataFilters} from "./notice.actions";


export function noticeDataFiltersReducer(): DynamicQueryPart & WithId{
    // TODO
    return;
}


const  _noticeDataReducer = createReducer(
    initialNoticeDataState,
    on(searchNoticeDataSuccess, (_, a) =>
        Object.assign([...a.result ? a.result : []], { _id: a._id})),
    on(searchNoticeDataError, (_1, a) => Object.assign([], {_id: a._id})),
  );

export function noticeDataReducer(): NoticeData[] & WithId{
    return _noticeDataReducer(state, action);
}
