import { Component, OnDestroy, OnInit } from "@angular/core";
import { NgForm } from "@angular/forms";

import { AppState} from "src/app/redux/state";
import { Action, Store } from "@ngrx/store";

import {OfficesData} from "src/app/redux/offices/offices.state";
import { selectOfficesData } from "src/app/redux/offices/offices.selectors";

import { environment } from "src/environments/environment";
import { of } from "rxjs/internal/observable/of";
import { ActivatedRoute, Router } from "@angular/router";
import { Observable, firstValueFrom} from "rxjs";
import { getRandomId } from "src/app/redux/actions";
import { addOfficesData } from "src/app/redux/offices/offices.actions";

@Component({
    selector: "app-officesManagement",
    templateUrl: "./officesManagement.component.html",
    styleUrls: ["./officesManagement.component.scss"],
})

export class OfficesManagementComponent  implements OnInit, OnDestroy{ 

    officeData$: Observable<OfficesData[]> = of([]);

    public editedOffices: Partial<OfficesData> = {};

    office: Partial<OfficesData> = {
        officesName : '',
        officesCapacity : null,  
        userId: null,
    };

    officesType: any = [ 
        { officesType: "Ufficio standard" },
        { officesType: "Sala riunioni" },
        { officesType: "Sala ristoro" },
        { officesType: "Ufficio amministrativo" },
        { officesType: "Ufficio dirigenti" },
    ];
    
    selectedType: any = null;

    //used to blocking some offices for only some user
    usersId: any[] = [  //to fill with keyclock users VIP or Amministrazione
        { usersId: "Utente 1" },
        { usersId: "Utente 2" },
        { usersId: "Utente 3" },
    ];

    selectedUsers: any[] = [];

    showAddForm = true;
    successAdd: boolean = false;
    successUpdate: boolean = false;

    constructor(
        private store: Store<AppState>,
        private router: Router,
        private route: ActivatedRoute
    ){
        this.officeData$ = store.select(selectOfficesData);
    }

    //add officeData
    addOffice(form: NgForm) {
        if(this.selectedType) {
            this.office.officesType = this.selectedType.officesType;
        }

        if (this.selectedUsers.length > 0) {
            const selectedUsersIds: string[] = this.selectedUsers.map((user) => user.usersId);
            const usersIdAsString: string = selectedUsersIds.join(", ");
            this.office.userId = usersIdAsString;
        }

        console.log(this.office);
        this.store.dispatch( addOfficesData({ item: this.office, _id: getRandomId() }) );
    
        this.resetFormFields(form);
        this.successAdd = true;
        this.successUpdate = false;
    }

    resetFormFields(form: NgForm) {
        form.resetForm();
        this.office = {
            officesName : '',
            officesCapacity : 1,
        };
        this.selectedType = null;
        this.selectedUsers = []
    }

    onTypeChange(newType: string) {
        this.selectedType = newType;
        console.log('Selected Type:', this.selectedType);
    }
 
    //delete officeData

    //update officeData


    ngOnInit(): void {
        
    }

    ngOnDestroy(): void {
        
    }
}
