import { Injectable } from '@angular/core';
import { ofType, createEffect, Actions } from '@ngrx/effects';
import { map, mergeMap, catchError } from 'rxjs/operators';
import { CustomHttpClient } from '../../services/custom-http-client.service';
import { environment } from '../../../environments/environment';  //  src/environments/environment';
import { changeNoticeDataFilters, searchNoticeData, searchNoticeDataError, searchNoticeDataSuccess} from './notice.actions';
import { addNoticeData, addNoticeDataSuccess, addNoticeDataError} from "./notice.actions";
import { NoticeData } from './notice.state';
import { HttpErrorResponse } from '@angular/common/http';
import { of } from 'rxjs/internal/observable/of';

@Injectable()
export class NoticeEffect{

    constructor(
        private actions$: Actions,
        private httpClient: CustomHttpClient
    ){ }


    //search NoticeData
    _serachNoticeData = createEffect(
        () => this.actions$.pipe(
            ofType(searchNoticeData),
            mergeMap(a => {
                const response$ = this.httpClient.post<NoticeData[]>(`${environment.apiUrl}/notice/searchNoticeData`, a.queryParams, { responseType: 'json'});
                return response$.pipe(
                    map(r => r ?? []),
                    map(r => searchNoticeDataSuccess({result: r, _id: a._id })),
                    catchError((err: HttpErrorResponse, _) => {
                        return [searchNoticeDataError({error: err.message, _id: a._id})];
                    }));
            }))
    );

    _changeNoticeDataFilters = createEffect(
        () => this.actions$.pipe(
            ofType(changeNoticeDataFilters),
            map(a => searchNoticeData({queryParams: a.queryParams, _id: a._id}))
            )
    );

    //add NoticeData
/*
    addNoticeData$ = createEffect(() =>
        this.actions$.pipe(
            ofType(addNoticeData),
            mergeMap((action) =>
                NoticeData.addItem(action.item).pipe(
                    map(() => addNoticeDataSuccess()),
                    catchError((error) => of(addNoticeDataError({ error })))
                )
            )
        )
    );*/

    addNoticeData$ = createEffect(() =>
        this.actions$.pipe(
            ofType(addNoticeData),
            mergeMap((a) =>
                this.NoticeData.addItem(a.item).pipe( // Utilizza il servizio NoticeDataService
                    mergeMap(() => [addNoticeDataSuccess()]), // Emetti l'azione di successo dopo l'aggiunta
                    catchError((error) => of(addNoticeDataError({ error })))
                )
            )
        )
    );



}
