import { Component, OnDestroy, OnInit } from "@angular/core";
import { Observable, of } from "rxjs";
import { InputTextModule } from 'primeng/inputtext';
import { InputTextareaModule } from 'primeng/inputtextarea'

import { NoticeData } from "src/app/redux/notice/notice.state";
import {FormControl, Validators, FormsModule, ReactiveFormsModule} from '@angular/forms';


@Component({
    selector: "app-noticesManagement",
    templateUrl: "./noticesManagement.component.html",
    styleUrls: ["./noticesManagement.component.scss"],
    imports:[InputTextModule, InputTextareaModule],
    
})

export class NoticesManagementComponent  { 

    noticeData$: Observable<NoticeData[]> = of([]);
    private usersIdAsString = "";

    notification = {
        title: "",
        object: "",
        description: ""
    };    

    usersId: any[] = [  
        { usersId: "Utente 1" },
        { usersId: "Utente 2" },
        { usersId: "Utente 3" },
        { usersId: "Utente 4" },
        { usersId: "Utente 5" },
        { usersId: "Utente 6" },
    ];

    selectedUsers: any[] = [];

    /*onSubmit() {
        console.log("Notifica aggiunta:", this.notification);
    }*/

    addNotice(notice:NoticeData){
        notice.noticeId;
        notice.noticeState;
        notice.noticeDateTime = Date.now;
        notice.noticeTitle = this.notification.title;
        notice.noticeObject = this.notification.object;
        notice.noticeDesc = this.notification.description;

        for (let id in this.selectedUsers){
            this.usersIdAsString += id;
        }
        notice.userId = this.usersIdAsString;

        console.log("Notifica aggiunta:", notice);
    }

    deleteNotice(notice:NoticeData){
        // onCascade a tutti quelli a cui è stata inviata
    }


      
}
