import { Component, OnInit } from "@angular/core";
import { environment } from "src/environments/environment";
import { CustomOAuthService } from "./services/custom-oauth.service";

@Component({
    selector: "app-menu",
    template: `
        <div class="menu">
            <ul class="layout-menu">
                <li
                    app-menuitem
                    *ngFor="let item of model; let i = index"
                    [item]="item"
                    [index]="i"
                    [root]="true"
                ></li>
            </ul>
        </div>
    `,
})
export class AppMenuComponent implements OnInit {
    model: any[];

    constructor(private oauthService: CustomOAuthService) {}

    async ngOnInit() {
        if (environment.onlyDesignMode) {
            this.model = [
                {
                    label: "Home",
                    icon: "pi pi-fw pi-home",
                    routerLink: ["/home"],
                }
            ];
        } else if (await this.oauthService.isAdminUser()) {
            this.model = [
                {
                    label: "Home",
                    icon: "pi pi-fw pi-home",
                    routerLink: ["/home"],
                }
            ];
        } else if (await this.oauthService.isBaseUser()) {
            this.model = [
                {
                    label: "Home",
                    icon: "pi pi-fw pi-home",
                    routerLink: ["/home"],
                }
            ];
        } else {
            this.model = [];
        }
    }
}
