import { Injectable, NgZone } from "@angular/core";
import { Resolve, ActivatedRouteSnapshot, RouterStateSnapshot } from "@angular/router";
import { Store } from "@ngrx/store";
import { Observable } from "rxjs";
import { skipWhile, take } from "rxjs/operators";
import { searchSampleData } from "../redux/sample/sample.actions";
import { SampleData } from "../redux/sample/sample.state";
import { AppState } from "../redux/state";
import { selectSampleData, selectSampleDataFilters } from "../redux/sample/sample.selectors";
import { environment } from "src/environments/environment";

@Injectable({ providedIn: "root" })
export class SampleResolver implements Resolve<SampleData[]> {
    constructor(
        private store: Store<AppState>,
        private zone: NgZone
    ) {}

    resolve(
        route: ActivatedRouteSnapshot,
        state: RouterStateSnapshot
    ): Observable<SampleData[]> | Promise<SampleData[]> | SampleData[] {
        const rand = Math.floor(Math.random() * 1_000_000).toFixed();
        this.zone.runOutsideAngular(() => {
            setTimeout(() => {
                this.store.select(selectSampleDataFilters).pipe(take(1)).subscribe((storedDqp) => {
                    this.store.dispatch(
                        searchSampleData({
                            _id: rand,
                            queryParams: {
                                filtering: storedDqp.filtering ? storedDqp.filtering : [],
                                paging: storedDqp.paging ? storedDqp.paging : { skip: 0, take: environment.defaultNumberOfRowsPerPage },
                                ordering: storedDqp.ordering ? storedDqp.ordering : [{ column: "sampleDataId", columnPrefix: "", descending: false }],
                            },
                        })
                    );
                });
            });
        });
        return this.store.select(selectSampleData).pipe(
            skipWhile((s) => s._id !== rand),
            take(1)
        );
    }
}
